    blackbox: true,
    optional: true
  },
  parser: {
    type: String
  },
  language: {
    type: String,
    optional: true,
    defaultValue: "en"
  },
  source: {
    type: String,
    optional: true
  }
};

export const ReactLayout = new SimpleSchema({
  ...sharedFields,
  templateFor: {
    type: [String],
    optional: true
  },
  template: {
    type: [Object],
    optional: true,
    blackbox: true
  }
});

export const Template = new SimpleSchema({
  ...sharedFields,
  template: {
    type: String,
    optional: true
  }
});
