    classes = classnames({
      "rui": true,
      "font-icon": true
    }, classes, this.props.className);

    return (
      <i style={this.props.style} className={classes} />
    );
  }
}

Icon.propTypes = {
  className: PropTypes.oneOfType([PropTypes.string, PropTypes.object]),
  icon: PropTypes.string.isRequired,
  style: PropTypes.object
};

export default Icon;
